package com.example.crm.model;

import org.springframework.data.annotation.Id; import org.springframework.data.mongodb.core.mapping.Document; import java.util.Map;

@Document("tenant_settings") public class TenantSettings { @Id private String id; private String tenantId; private String region; private Storage storage = new Storage(); private Email email = new Email(); private Calendar calendar = new Calendar(); private Rbac rbac = new Rbac(); private Map<String,Boolean> features; public String getId(){return id;} public void setId(String id){this.id=id;} public String getTenantId(){return tenantId;} public void setTenantId(String t){this.tenantId=t;} public String getRegion(){return region;} public void setRegion(String r){this.region=r;} public Storage getStorage(){return storage;} public void setStorage(Storage s){this.storage=s;} public Email getEmail(){return email;} public void setEmail(Email e){this.email=e;} public Calendar getCalendar(){return calendar;} public void setCalendar(Calendar c){this.calendar=c;} public Rbac getRbac(){return rbac;} public void setRbac(Rbac r){this.rbac=r;} public Map<String,Boolean> getFeatures(){return features;} public void setFeatures(Map<String,Boolean> f){this.features=f;} public static class Storage { public String provider="s3"; public String bucket; public String basePath; } public static class Email { public String provider="microsoft-graph"; public String mode="delegated"; public String sharedMailbox; public Integer throttlePerMin=60; } public static class Calendar { public String provider="microsoft-graph"; public boolean sync=true; public String defaultCalendar; } public static class Rbac { public String defaultVisibility="TEAM"; public boolean allowManualShare=true; } }
