package com.example.crm.model;

import org.springframework.data.annotation.Id; import org.springframework.data.mongodb.core.index.Indexed; import org.springframework.data.mongodb.core.mapping.Document; import java.time.Instant; import java.util.HashSet; import java.util.Set;

@Document("users") public class User { @Id private String id; @Indexed private String tenantId; @Indexed(unique=true) private String email; private String passwordHash; private String fullName; private Set<String> roles = new HashSet<>(); private Instant createdAt = Instant.now();
  public String getId(){return id;} public void setId(String id){this.id=id;} public String getTenantId(){return tenantId;} public void setTenantId(String t){this.tenantId=t;} public String getEmail(){return email;} public void setEmail(String e){this.email=e;} public String getPasswordHash(){return passwordHash;} public void setPasswordHash(String p){this.passwordHash=p;} public String getFullName(){return fullName;} public void setFullName(String f){this.fullName=f;} public Set<String> getRoles(){return roles;} public void setRoles(Set<String> r){this.roles=r;} public Instant getCreatedAt(){return createdAt;} public void setCreatedAt(Instant c){this.createdAt=c;} }
